#define UNUSED(x)               ((void)(x))
#define NULL                    ((void *)0)
#define RREG(addr)              (*((volatile uint32_t *)((common_addr_t)(addr))))
#define WREG(addr, value)       (*((volatile uint32_t *)((common_addr_t)(addr))) = (value))
#define RREG_8(addr)            (*((volatile uint8_t *)((common_addr_t)(addr))))
#define WREG_8(addr, value)     (*((volatile uint8_t *)((common_addr_t)(addr))) = (value))

/* Wrapper of fsl_memcpy created to satisfy compiler created reference to memcpy. */
/* This is just temporary fix. Issue AAVB-1070 */
void *fsl_memcpy (void *pavDest2, const void *pcoavSource2,uint32_t u32Length2);
static void *memcpy(void *dest, const void *src, unsigned n)
{
    return fsl_memcpy(dest, src, n);
}

